function run(input, params) {

    var root = Repository.GetDocument(null, {
        "value" : "/"
    });

    var newDoc = Document.Create(root, {
        "type" : "File",
        "name" : "newDoc",
        "properties" : {
            "dc:title" : "New Title",
            "dc:source" : "JavaScript",
            "dc:subjects" : [ "from", "javascript" ]
        }
    });

    Document.SetBlob(newDoc, {
      'file': input,
      'save': true
    });

    return newDoc;
}

